var React = require('react');
var classNames = require('classnames');

module.exports = function(component) {

  var classes = classNames('input-group', {
    'component-input': true,
    'component-credit-card-number': true,
    'input-group-error': component.state.error || false
  });

  var label, visa, mastercard, amex, discover, images, error;

  if (component.props.label){
    label = ( <label className="input-group-label credit-card-number-label" htmlFor={component.props.id}>{component.props.label}</label> );
  }

  if(component.props.showImages){

    if(component.props.accepted.indexOf('visa') > -1){
      visa = ( <img className={component.state.cardType == 'visa' ? 'card-image visa active' : 'card-image visa disabled'} src="data:image/png;base64," /> );
    }

    if(component.props.accepted.indexOf('mastercard') > -1){
      mastercard = ( <img className={component.state.cardType == 'mastercard' ? 'card-image master-card active' : 'card-image master-card disabled' } src="data:image/png;base64," /> );
    }

    if(component.props.accepted.indexOf('amex') > -1){
      amex = ( <img className={component.state.cardType == 'amex' ? 'card-image american-express active' : 'card-image american-express disabled' } src="data:image/png;base64," /> );
    }

    if(component.props.accepted.indexOf('discover') > -1){
      discover = ( <img className={component.state.cardType == 'discover' ? 'card-image discover active' : 'card-image discover disabled' } src="data:image/png;base64," /> );
    }

    images = ( <div className="credit-card-images">{visa} {mastercard} {amex} {discover}</div> );
  }

  if (component.state.error){
    error = ( <span className="component-input-error">{component.state.error}</span> );
  }

  return (
    <div className={classes}>
      {images}
      <div className="credit-card-number">
        {label}
        <input
          className={( !component.state.valid ) ? 'credit-card-number-input has-error' : 'credit-card-number-input' }
          type="text"
          id={component.props.id}
          name={component.props.name}
          autoComplete="off"
          minLength="13"
          maxLength="19"
          onChange={component.handleChange}
          defaultValue={component.props.defaultValue}
          placeholder={component.props.placeHolder} />
        {error}
      </div>
    </div>
  );
};
